sysevent_in_email_action.script:
(function runAction(/*GlideRecord*/ current, /*GlideRecord*/ event, /*EmailWrapper*/ email, /*ScopedEmailLogger*/ logger, /*EmailClassifier*/ classifier) {

	// Implement email action here

	// add the inbound email body to the Nedit Task record work notes field, then update the //database. Values written to the Work notes field appear in the Activities field and not to the //work notes field on the form.
		current.work_notes = email.body_text;
	current.update();
})(current, event, email, logger, classifier);
